pragma solidity ^0.5.0;

contract TodoList {
    uint public taskCount = 0;

    struct Task {
        uint id;
        string content;
        bool completed;
        uint dueDate;
    }

    mapping(uint => Task) public tasks;

    event TaskCreated(
        uint id,
        string content,
        bool completed,
        uint dueDate
    );

    event TaskCompleted(
        uint id,
        bool completed
    );

    event TaskDeleted(
        uint id
    );

    constructor() public {
        createTask("Check out dappuniversity.com", now + 1 days);
    }

    function createTask(string memory _content, uint _dueDate) public {
        taskCount += 1;
        tasks[taskCount] = Task(taskCount, _content, false, _dueDate);
        emit TaskCreated(taskCount, _content, false, _dueDate);
    }

    function toggleCompleted(uint _id) public {
        Task memory _task = tasks[_id];
        _task.completed = !_task.completed;
        tasks[_id] = _task;
        emit TaskCompleted(_id, _task.completed);
    }

    function deleteTask(uint _id) public {
        delete tasks[_id];
        emit TaskDeleted(_id);
    }

}
